/*Table structure for table `ex_posts` */

DROP TABLE IF EXISTS `ex_posts`;

CREATE TABLE `ex_posts` (
  `ID` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `post_author` bigint(20) unsigned NOT NULL DEFAULT '0',
  `post_date` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_date_gmt` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_content` longtext NOT NULL,
  `post_title` text NOT NULL,
  `post_excerpt` text NOT NULL,
  `post_status` varchar(20) NOT NULL DEFAULT 'publish',
  `comment_status` varchar(20) NOT NULL DEFAULT 'open',
  `ping_status` varchar(20) NOT NULL DEFAULT 'open',
  `post_password` varchar(20) NOT NULL DEFAULT '',
  `post_name` varchar(200) NOT NULL DEFAULT '',
  `to_ping` text NOT NULL,
  `pinged` text NOT NULL,
  `post_modified` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_modified_gmt` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_content_filtered` longtext NOT NULL,
  `post_parent` bigint(20) unsigned NOT NULL DEFAULT '0',
  `guid` varchar(255) NOT NULL DEFAULT '',
  `menu_order` int(11) NOT NULL DEFAULT '0',
  `post_type` varchar(20) NOT NULL DEFAULT 'post',
  `post_mime_type` varchar(100) NOT NULL DEFAULT '',
  `comment_count` bigint(20) NOT NULL DEFAULT '0',
  PRIMARY KEY (`ID`),
  KEY `post_name` (`post_name`),
  KEY `type_status_date` (`post_type`,`post_status`,`post_date`,`ID`),
  KEY `post_parent` (`post_parent`),
  KEY `post_author` (`post_author`)
) ENGINE=InnoDB AUTO_INCREMENT=253 DEFAULT CHARSET=utf8;

/*Data for the table `ex_posts` */

insert  into `ex_posts`(`ID`,`post_author`,`post_date`,`post_date_gmt`,`post_content`,`post_title`,`post_excerpt`,`post_status`,`comment_status`,`ping_status`,`post_password`,`post_name`,`to_ping`,`pinged`,`post_modified`,`post_modified_gmt`,`post_content_filtered`,`post_parent`,`guid`,`menu_order`,`post_type`,`post_mime_type`,`comment_count`) values (1,2,'2014-12-14 10:08:45','2014-12-14 10:08:45','Welcome to WordPress. This is your first post. Edit or delete it, then start blogging!','Hello world!','','publish','open','open','','hello-world','','','2014-12-14 10:08:45','2014-12-14 10:08:45','',0,'http://edex.dev/?p=1',0,'post','',1),(2,2,'2014-12-14 10:08:45','2014-12-14 10:08:45','[vc_row el_class=\"contact-person-box\" css=\".vc_custom_1436072535141{background-color: #ffffff !important;}\"][vc_column width=\"1/1\"][vc_single_image image=\"225\" alignment=\"center\" border_color=\"grey\" img_link_target=\"_self\" img_size=\"small\"][vc_column_text]\r\n<h2 style=\"text-align: center;\">About Edex</h2>\r\n<p style=\"text-align: center;\">We are a complete end to end solution for education providers - we get students to look at your website, make an enquiry, help your conversions and enrolments and take your business offshore. Regulatory requirements and audits as well as getting you on the register. Our business is to help take your business into new markets and increase your student enrolments. A straight talking, common sense approach to the big issues facing your business and offering the solutions required to grow.</p>\r\n[/vc_column_text][/vc_column][/vc_row][vc_row el_class=\"image-left-box\" css=\".vc_custom_1436073168392{background-color: #e6e6e6 !important;}\"][vc_column width=\"1/2\"][vc_single_image image=\"231\" border_color=\"grey\" img_link_target=\"_self\" img_size=\"full\" alignment=\"center\"][/vc_column][vc_column width=\"1/2\"][vc_column_text]\r\n<h3>What edex do?</h3>\r\nWe are a complete end to end solution for education providers - we get students to look at your website, make an enquiry, help your conversions and enrolments and take your business offshore. Regulatory requirements and audits as well as getting you on the register. Our business is to help take your business into new markets and increase your student enrolments. A straight talking, common sense approach to the big issues facing your business and offering the solutions required to grow.[/vc_column_text][vc_button title=\"Contact us\" target=\"_self\" color=\"btn-dark-grey\" icon=\"contact-person\" href=\"http://edex.martintutko.com/contact-us/\"][/vc_column][/vc_row][vc_row el_class=\"image-right-box\" css=\".vc_custom_1436074184384{background-color: #ffffff !important;}\"][vc_column width=\"1/2\"][vc_single_image image=\"232\" border_color=\"grey\" img_link_target=\"_self\" img_size=\"full\" alignment=\"center\"][/vc_column][vc_column width=\"1/2\"][vc_column_text]\r\n<h3>How do we do it?</h3>\r\nWe ask the right questions to build a collaborative approach to help your business, after all your needs are different to other schools and your students want something different. Identifying who your customers are, the reasons the make decisions and helping to improve enrolments is how we measure our success. We stay ahead of market trends to help you navigate the uncertainities of going offshore and start really scaling your growth.[/vc_column_text][vc_button title=\"Learn more about our services\" target=\"_self\" color=\"btn-dark-grey\" icon=\"arrow-with-circle\" href=\"http://edex.martintutko.com/services/\"][/vc_column][/vc_row]','About us','','publish','open','closed','','about-us','','','2015-07-07 08:47:42','2015-07-07 08:47:42','',0,'http://edex.dev/?page_id=2',0,'page','',0),(12,2,'2014-12-16 02:20:34','2014-12-16 02:20:34','[vc_row el_class=\".contact-person-box\" css=\".vc_custom_1436170141805{background-color: #ffffff !important;}\"][vc_column width=\"1/1\"][vc_single_image image=\"212\" alignment=\"center\" border_color=\"grey\" img_link_target=\"_self\" img_size=\"small\"][vc_column_text]\r\n<h2 style=\"text-align: center;\">Our Services</h2>\r\n<p style=\"text-align: center;\">We can take the load off of you, and tailor the best solution for you and your college. We decode the choices to provide simple common sense tactics to grow your business.</p>\r\n[/vc_column_text][/vc_column][/vc_row][vc_row css=\".vc_custom_1436171277775{background-color: #ebebeb !important;}\" el_class=\".image-left-box\"][vc_column width=\"1/2\"][vc_single_image image=\"216\" style=\"vc_box_rounded\" border_color=\"grey\" img_link_target=\"_self\" img_size=\"full\"][/vc_column][vc_column width=\"1/2\"][vc_column_text]\r\n<h3>Market Research</h3>\r\nYou need intelligent information, curated content that is distilled into a format that is easily digested and helps you make decisions quickly. Our approach to research is to take the work off you, and combine the data and synthesise this into information. Studies, market reports, online analytics, trends, sales, marketing, competitor performance all combined into the right format that help your grow your business and help you make intelligent forecasts.[/vc_column_text][vc_button title=\"Contact us\" target=\"_self\" color=\"btn-dark-grey\" icon=\"contact-person\" href=\"http://edex.martintutko.com/contact-us/\"][/vc_column][/vc_row][vc_row css=\".vc_custom_1436170388734{background-color: #ffffff !important;}\" el_class=\".image-right-box\"][vc_column width=\"1/2\"][vc_single_image image=\"218\" style=\"vc_box_rounded\" border_color=\"grey\" img_link_target=\"_self\" img_size=\"full\"][/vc_column][vc_column width=\"1/2\"][vc_column_text]\r\n<h3>Business Development</h3>\r\nIn education relationships matter, and every relationship requires the right introduction and the great communication. We take the worry out of getting you in front of the right people who will work with you to grow your business. Our extensive network of partners, institutions and agents will be opened to you and we can help you commence building your pipeline. Our reputation can open the doors to help stakeholders trust you and your brand.[/vc_column_text][vc_button title=\"Contact us\" target=\"_self\" color=\"btn-dark-grey\" icon=\"contact-person\" href=\"http://edex.martintutko.com/contact-us/\"][/vc_column][/vc_row][vc_row css=\".vc_custom_1436171712027{background-color: #c7eade !important;}\" el_class=\".image-left-box\"][vc_column width=\"1/2\"][vc_single_image image=\"229\" style=\"vc_box_rounded\" border_color=\"grey\" img_link_target=\"_self\" img_size=\"full\"][/vc_column][vc_column width=\"1/2\"][vc_column_text]\r\n<h3>Digital Intelligence</h3>\r\nYou\'re a digital immigrant trying to work with digital natives, and just like speaking another language there\'s being able to speak another language with some basic conversational phrases and being able to speak another language. We know where your customers live, where they spend their time and how they like to access information to make their decisions about studying. We do the deep dive on social media channels, customer profiling and write the communications that will resonate with prospective students and get them to make an enquiry and go onto enrol.[/vc_column_text][/vc_column][/vc_row][vc_row css=\".vc_custom_1436170388734{background-color: #ffffff !important;}\" el_class=\".image-right-box\"][vc_column width=\"1/2\"][vc_single_image image=\"220\" style=\"vc_box_rounded\" border_color=\"grey\" img_link_target=\"_self\" img_size=\"full\"][/vc_column][vc_column width=\"1/2\"][vc_column_text]\r\n<h3>Customer Management</h3>\r\nIt\'s easy to get people to look at your website and download a brochure, but this is only the first step. Your sales team, marketing team and customer service team all impact on your bottom line - and it is absolutely critical to make sure that your teams are all working together to keep the students after you convert the leads.[/vc_column_text][vc_button title=\"Contact us\" target=\"_self\" color=\"btn-dark-grey\" icon=\"contact-person\" href=\"http://edex.martintutko.com/contact-us/\"][/vc_column][/vc_row][vc_row el_class=\"contact-form-box\"][vc_column width=\"1/1\"][contact-form-7 id=\"79\" title=\"Contact Us\"][/vc_column][/vc_row]','Services','','publish','open','closed','','services','','','2015-07-07 08:43:55','2015-07-07 08:43:55','',0,'http://edex.dev/?page_id=12',0,'page','',0),(25,2,'2014-12-16 09:29:35','2014-12-16 09:29:35','This is an example page. It\'s different from a blog post because it will stay in one place and will show up in your site navigation (in most themes). Most people start with an About page that introduces them to potential site visitors. It might say something like this:\r\n<blockquote>Hi there! I\'m a bike messenger by day, aspiring actor by night, and this is my blog. I live in Los Angeles, have a great dog named Jack, and I like pia coladas. (And gettin\' caught in the rain.)</blockquote>\r\n...or something like this:\r\n<blockquote>The XYZ Doohickey Company was founded in 1971, and has been providing quality doohickeys to the public ever since. Located in Gotham City, XYZ employs over 2,000 people and does all kinds of awesome things for the Gotham community.</blockquote>\r\nAs a new WordPress user, you should go to <a href=\"http://edex.dev/wp-admin/\">your dashboard</a> to delete this page and create new pages for your content. Have fun!','About us','','inherit','open','open','','2-revision-v1','','','2014-12-16 09:29:35','2014-12-16 09:29:35','',2,'http://edex.dev/uncategorized/2-revision-v1/',0,'revision','',0),(27,2,'2014-12-16 09:31:45','2014-12-16 09:31:45','Blog','Blog','','publish','open','closed','','blog','','','2014-12-18 15:10:39','2014-12-18 15:10:39','',0,'http://edex.dev/?page_id=27',0,'page','',0),(28,2,'2014-12-16 09:31:45','2014-12-16 09:31:45','Blog','Blog','','inherit','open','open','','27-revision-v1','','','2014-12-16 09:31:45','2014-12-16 09:31:45','',27,'http://edex.dev/unca